namespace SpojeNet\CSas\Ui;

/**
 * Page Bottom.
 *
 * @author     Vitex <vitex@hippy.cz>
 *
 * @no-named-arguments
 */
class PageBottom extends \Ease\Html\FooterTag
{
    public function __construct()
    {
        parent::__construct();
        $this->addItem('<hr>');
        $footrow = new \Ease\TWB5\Row();

        $author = '<strong>CSAS Authorize</a></strong> '.\Ease\Shared::appVersion().'<br>&nbsp;&nbsp; &copy; 2024-2025 <a href="https://spojenet.it/">SpojeNetIT s.r.o.</a>';

        $footrow->addColumn(6, [$author]);

        $github = new \Ease\Html\ATag('https://github.com/Spoje-NET/csas-authorize', new \Ease\Html\ImgTag('data:image/svg+xml;base64,', 'Github', ['title' => _('Github Project')]));

        $footrow->addColumn(2, $github);

        $this->addItem(new \Ease\TWB5\Container($footrow));
    }
}
